    const makeLine=()=> {
        return blanks(width + 1 - i) + frame +
            blanks(i * 2) + frame;
    }
while (i++ < width) {
        frame = String.fromCharCode(a + i);
        lines.push(makeLine());
    }
if (lines.length) {
        lines.unshift(blanks(width + 1) + 'A');
        lines = lines.concat(
            lines.slice(0, lines.length - 1).reverse());
    } else {lines.push('A');
    }
    console.log(lines)
    lines.join('\n');
    console.log(lines)
    setPatternArray(lines)
}






  return (
    <div className="App">
      <div>
        <input type="text" value={pattern} onChange={onchangePattern}/>
        <button onClick={onSubmitPattern}>Submit</button>
      </div>
      {patternArray.map(a=>{return <pre>{a}</pre>})}
    </div>
  );
};
export default App;
